mapper.select:
SELECT id, post_image_url, content
FROM post_content
WHERE post_id = #{id}

mapper.delete:
DELETE
FROM greenery.like_number
WHERE post_id = #{postId}
  AND member_id = #{memberId}

mapper.select:
SELECT member_id
FROM greenery.post
WHERE id = #{postId}

mapper.update:
UPDATE greenery.post
SET title = #{title}
WHERE id = #{postId}

UPDATE greenery.post_content
SET content        = #{postContent.content},
    post_image_url = #{postContent.postImageUrl}
WHERE post_id = #{postId}
  AND id = #{postContent.id}

mapper.select:
SELECT post_id
FROM like_number
WHERE member_id = #{userId}

SELECT p.id, p.title, p.hits, p.like_numbers AS likes, (SELECT post_image_url FROM post_content WHERE post_id = p.id LIMIT 1) AS mainImageUrl FROM post p
WHERE IN
#{postIds}
